
package FirstPackage;

public class JavaDemo {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		int a = 99; 
		int b = 10;
		
		//nested if else part
		if (a==b){
			System.out.println("a is equal to b; since a= "+a+ " b="+b);
			if (a%2==0){
				System.out.println("a is an Even Number");
			}
			else
				System.out.println("a is an odd Number");
			
		}
		else{
			System.out.println("a is NOT equal to b; since a= "+a+ " b="+b);
			if (a%2==0){
				System.out.println("a is an Even Number");
			}
			else
				System.out.println("a is an odd Number");
		}
		
		//nested if else part closed
		
		//switch case part
		
			

	}

	
}
